function update_price_filter(){const e=document.querySelectorAll(".range-input input"),t=document.querySelectorAll(".price-input input"),a=document.querySelector(".slider .progress");let l=3e4;t.forEach((r=>{r.addEventListener("input",(r=>{let u=parseInt(t[0].value),n=parseInt(t[1].value);n-u>=l&&n<=e[1].max&&("input-min"===r.target.className?(e[0].value=u,a.style.left=u/e[0].max*100+"%"):(e[1].value=n,a.style.right=100-n/e[1].max*100+"%"))}))})),e.forEach((r=>{r.addEventListener("input",(r=>{let u=parseInt(e[0].value),n=parseInt(e[1].value);n-u<l?"range-min"===r.target.className?e[0].value=n-l:e[1].value=u+l:(t[0].value=u,t[1].value=n,a.style.left=u/e[0].max*100+"%",a.style.right=100-n/e[1].max*100+"%")}))}))}update_price_filter();